package com.open.bright.feign.api;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.*;@FeignClient(name="talk-form")@RequestMapping("/talk/form/feign")public interface FormFeign {    @GetMapping(value = "/findByName")    String feignFindByName(@RequestParam("name") String name);    @PostMapping(value = "/login")    String login(@RequestBody String username);}