package com.open.bright.form.controller;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;import org.springframework.web.bind.annotation.RestController;@RestController@Api(tags = "辩则明-交互平台")@RequestMapping("/talk/form")public class FormController {    @GetMapping(value = "/findByName")    @ApiOperation(value = "根据名称查询")    public String findByName(@RequestParam("name") String name){        System.out.println("接收请求 findByName() 参数：" + name);        return name + "：SUCCESS！！！";    }    @GetMapping(value = "/feign/findByName")    @ApiOperation(value = "根据名称feign查询")    public String feignFindByName(@RequestParam("name") String name){        System.out.println("接收请求 feignFindByName() 参数：" + name);        return name + "：feign - SUCCESS！！！";    }}