package com.open.bright.form.controller;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.JSONObject;import io.swagger.annotations.Api;import io.swagger.annotations.ApiOperation;import org.springframework.web.bind.annotation.*;@RestController@Api(tags = "辩则明-交互平台")@RequestMapping("/talk/form")public class FormController {    @GetMapping(value = "/findByName")    @ApiOperation(value = "根据名称查询")    public String findByName(@RequestParam("username") String username){        System.out.println("接收请求 findByName() 参数：" + username);        return username + "：SUCCESS！！！";    }    @PostMapping(value = "/login")    @ApiOperation(value = "根据名称查询")    public String login(@RequestBody String username){        System.out.println("接收请求 findByName() 参数：" + username);        JSONObject user = new JSONObject();        user.put("username", username);        return user.toJSONString();    }    @GetMapping(value = "/feign/findByName")    @ApiOperation(value = "根据名称feign查询")    public String feignFindByName(@RequestParam("name") String name){        System.out.println("接收请求 feignFindByName() 参数：" + name);        return name + "：feign - SUCCESS！！！";    }}